function dct_generate_arrays(r){tmp_array1=[],tmp_array2=[];for(var a=0;a<=r.size-8;a+=8)tmp_array1.push([r.src_colors[a],r.src_colors[a+1],r.src_colors[a+2],r.src_colors[a+3],r.src_colors[a+4],r.src_colors[a+5],r.src_colors[a+6],r.src_colors[a+7]]);for(var a=0;a<r.size/64;a++)tmp_array2.push([tmp_array1[a],tmp_array1[a+8],tmp_array1[a+16],tmp_array1[a+24],tmp_array1[a+32],tmp_array1[a+40],tmp_array1[a+48],tmp_array1[a+56]]);for(var _=Math.sqrt(tmp_array2.length),a=0;a<=tmp_array2.length-_;a+=_){tmp_array3=[];for(var s=0;_>s;s++)tmp_array3.push(tmp_array2[a*_+s]);r.sub_arrays.push(tmp_array3)}}